import logo from './logo.svg';
import { motion } from "framer-motion"; 
import './App.css';
import { useState } from "react";

function App() {
  const [drink, setDrink] = useState(null);
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const fetchDrink = async () => {
    try {
      const response = await fetch("https://www.thecocktaildb.com/api/json/v1/1/random.php");
      console.log("Response Status:", response.status);

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
      const data = await response.json();
      console.log("API Response:", data.drinks);
      if (data.drinks && data.drinks.length > 0) {
        setDrink(data.drinks[0]); // Always set drink
        setPosition({
          x: Math.random() * 300 - 150, // Random X between -150 and 150
          y: Math.random() * 200 - 100, // Random Y between -100 and 100
        });
      } else {
        throw new Error("No drink data found!");
      }

    } catch (error) {
      console.log("Error catching:", error);
    }
  };
  return (
    <div className="container">
      <button className="drink-button" onClick={fetchDrink}>
        I'm Bored
      </button>

      {drink && (
        <motion.div
          className="drink-container"
          initial={{ opacity: 0, y: -20 }} // Start invisible and slightly up
          animate={{ opacity: 1, y: 0 }} // Fade in and slide down
          transition={{ duration: 0.5 }} // Animation duration
        >
          <h2>{drink.strDrink}</h2>
          <motion.img
            key={drink.idDrink}
            src={drink.strDrinkThumb}
            alt={drink.strDrink}
            className="drink-image"
            initial={{ y: -300, opacity: 0, rotate: 0 }} // Start above and no rotation
            animate={{ y: 0, opacity: 1, rotate: 9720 }} // Slam down, appear, then spin
            transition={{
              type: "spring",
              stiffness: 300, // Makes it slam down fast
              damping: 10, // Controls bounce effect
              duration: 0.6, // Controls overall timing
              rotate: { duration: 1, delay: 0.3, ease: "easeInOut" }, // 360° spin after slam
            }}
          />
          <motion.p
            key={drink.idDrink} // Reset animation on new drinks
            initial={{ x: 0, y: 0 }}
            animate={{ x: position.x, y: position.y }} // Move randomly
            transition={{
              type: "spring",
              stiffness: 100,
              damping: 8,
              repeat: Infinity, // Keep bouncing
              repeatType: "reverse", // Bounce back and forth
              duration: 1.5, // Control speed of movement
            }}
            className="instructions"
          >
            <strong>Instructions:</strong> {drink.strInstructions}
          </motion.p>
        </motion.div>
      )}
    </div>
  );
}

export default App;
