import logo from './logo.svg';
import { motion } from "framer-motion"; 
import './App.css';
import { useState } from "react";

function App() {
  const [drink, setDrink] = useState(null);
  const [position, setPosition] = useState({ x: 0, y: 0 });

  const fetchDrink = async () => {
    try {
      const response = await fetch("https://www.thecocktaildb.com/api/json/v1/1/random.php");
      console.log("Response Status:", response.status);

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
      const data = await response.json();
      console.log("API Response:", data.drinks);
      if (data.drinks && data.drinks.length > 0) {
        setDrink(data.drinks[0]); // Always set drink
        setPosition({
          x: Math.random() * 300, // Random X between -150 and 150
          y: Math.random() * 200, // Random Y between -100 and 100
        });
      } else {
        throw new Error("No drink data found!");
      }

    } catch (error) {
      console.log("Error catching:", error);
    }
  };
  return (
    <div className="container">
    <h1>üçπ Random Drink Generator</h1>

    <button className="drink-button" onClick={fetchDrink}>
      Get a Random Drink
    </button>

    {/* Wrap everything inside a single motion.div so it fully replaces previous drink */}
    <motion.div key={drink?.idDrink} className="drink-wrapper">
      {drink && (
        <motion.div
          className="drink-container"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }} // Ensures smooth replacement
          transition={{ duration: 0.5 }}
        >
          <h2>{drink.strDrink}</h2>

          {/* üî• Slam down & spin effect for image */}
          <motion.img
            src={drink.strDrinkThumb}
            alt={drink.strDrink}
            className="drink-image"
            initial={{ y: -300, opacity: 0, rotate: 0 }}
            animate={{ y: 0, opacity: 1, rotate: 360 }}
            transition={{
              type: "spring",
              stiffness: 300,
              damping: 10,
              duration: 0.6,
              rotate: { duration: 1, delay: 0.3, ease: "easeInOut" },
            }}
          />

          {/* üî• Instructions text bounces continuously */}
          <motion.p
            initial={{ x: 0, y: 0 }}
            animate={{ x: position.x, y: position.y }}
            transition={{
              type: "spring",
              stiffness: 100,
              damping: 8,
              repeat: Infinity,
              repeatType: "reverse",
              duration: 1.5,
            }}
            className="instructions"
          >
            <strong>Instructions:</strong> {drink.strInstructions}
          </motion.p>
        </motion.div>
      )}
    </motion.div>
  </div>
  );
}

export default App;
